  const[loading, setLoading] = useState(false);
  const URL = `https://shot.screenshotapi.net/screenshot?token=${API_KEY}&url=${search}&full_page=true&output=image&file_type=png&wait_for_event=load`

  const getScreenshots = async () => {
    setSearch("");
    setEror(false);
    setLoading(true);
    const response = await fetch(URL);
    if(response.ok){
      setImg(response);
      setLoading(false);
    }else{
      setEror(false);
    }
  }

    function SearchScreenshots(){
