import { useEffect, useState } from 'react';
import './App.css';

const App = () => {
  const API_KEY ="G6T5BQZ-YC4M1NN-KK29KWN-0SNE1VX";
  const[search, setSearch] = useState("");
  const[img, setImg] = useState("");
  const[eror, setEror] = useState(false);
  const[loading, setLoading] = useState(false);
  const URL = `https://shot.screenshotapi.net/screenshot?token=${API_KEY}&url=${search}&full_page=true&output=image&file_type=png&wait_for_event=load`

  const getScreenshots = async () => {
    setSearch("");
    setEror(false);
    setLoading(true);
    const response = await fetch(URL);
    if(response.ok){
      setImg(response);
      setLoading(false);
    }else{
      setEror(true);
    }
  }

  useEffect(()=>{
    setSearch("");
    getScreenshots();
  },[])

    const SearchScreenshots = e=>{
      e.preventDefault();
      getScreenshots();
    }

  return (
    <div className="App">
      <nav className='ko'>
      <form onSubmit={SearchScreenshots}>
        <input
        autoFocus
        type="text"
        value={search}
        onChange={(e)=>setSearch(e.target.value)}
        />
        <button type='submit'>SUBMIT</button>
      </form>
      </nav>
    </div>
  );
}

export default App;
